mapper.select:
/* selectUser */
SELECT *
  FROM TB_USER
 WHERE USER_ID = #{userId}

mapper.insert:

/* insertFileInfo */
INSERT INTO TB_FILE (
    FILE_ID,
    ORG_FILE_NAME,
    REAL_FILE_NAME,
    PATH
) VALUES (
    FILE_SEQ.NEXTVAL,
    #{orgFileName},
    #{realFileName},
    #{path}
)

mapper.update:
UPDATE TB_FILE
   SET ORG_FILE_NAME = #{orgFileName}
     , REAL_FILE_NAME = #{realFileName}
     , PATH = #{path}
     , MOD_DATE = SYSDATE
 WHERE FILE_ID = #{fileId}

UPDATE TB_NOTICE
   SET USER_ID = #{userId}
     , TITLE = #{title}
     , CONTENT = #{content}
     , FILE_ID = #{fileId}
     , MOD_DATE = SYSDATE
 WHERE NOTICE_ID = #{noticeId}

mapper.select:
/* selectNotices */ SELECT T.* FROM ( SELECT TT.*, ROWNUM RNUM FROM (
SELECT A.NOTICE_ID , A.USER_ID , A.TITLE , TO_CHAR(A.CONTENT) CONTENT , A.VIEWS , A.REG_DATE , A.MOD_DATE , (SELECT NAME FROM TB_USER WHERE A.USER_ID = USER_ID ) NAME , B.FILE_ID , B.ORG_FILE_NAME , B.REAL_FILE_NAME , B.PATH FROM TB_NOTICE A LEFT OUTER JOIN TB_FILE B ON A.FILE_ID = B.FILE_ID WHERE ORDER BY ${sortCol} ${sortKeyword}
) TT ) T WHERE ${(pageNo-1)*cntPerPage+1} <= T.RNUM AND T.RNUM <= ${pageNo*cntPerPage}
