mapper.select:
/* selectProducts */
SELECT T.* FROM ( SELECT TT.*, ROWNUM RNUM FROM (

    SELECT A.*
         , (SELECT RTRIM(
               (SELECT LISTAGG(PATH || REAL_FILE_NAME || ',') WITHIN GROUP(ORDER BY REAL_FILE_NAME)
                  FROM TB_FILE
                 WHERE PRODUCT_ID = A.PRODUCT_ID
               )
               , ',') FROM DUAL
           ) IMAGES
      FROM TB_PRODUCT A
     WHERE TO_NUMBER(${startPrice}) <= A.PRICE
       AND A.PRICE <= TO_NUMBER(${endPrice})
       AND (
               A.TITLE LIKE '%' || (CASE WHEN #{searchTerm} IS NULL THEN A.TITLE ELSE #{searchTerm} END) || '%'
            OR A.DESCRIPTION LIKE '%' || (CASE WHEN #{searchTerm} IS NULL THEN A.DESCRIPTION ELSE #{searchTerm} END) || '%'
           )
     ORDER BY ${sortBy} ${order}

) TT ) T WHERE ${skip} <= T.RNUM AND T.RNUM < ${skip + limit}

/* selectCartProducts */
SELECT A.*
     , (SELECT RTRIM(
            (SELECT LISTAGG(PATH || REAL_FILE_NAME || ',') WITHIN GROUP(ORDER BY REAL_FILE_NAME)
               FROM TB_FILE
              WHERE PRODUCT_ID = A.PRODUCT_ID
            )
            , ',') FROM DUAL
     ) IMAGES
  FROM TB_PRODUCT A
 WHERE A.PRODUCT_ID IN

            #{productId}
